// ##############################
// // // general variables for charts
// #############################
const primaryColor = "#26a69a";
//const accentColor = "#ff8a65";
const purpleColor = "#7f8ff4";
const redColor = "#fb6373";

//const chartColor = "#FFFFFF";

// ##############################
// // // Dashboard view - Bar Chart - Card
// #############################

